mapper.select:
SELECT u.id,u.username,u.password,u.email,u.gender,u.qq,u.weixin,u.idcard, t.name sysname, (SELECT name FROM systemdef psys WHERE psys.id=t.ptopid) psyname FROM sysuser u,systemdef t WHERE u.sysid=t.id
AND (u.sysid=t.id OR u.sysid=t.parentid OR u.sysid=t.ptopid) AND (t.id=#{csysid} OR t.parentid=#{csysid} OR t.ptopid=#{csysid})
ORDER BY #{page.sort},#{page.order}
limit #{page.start},#{page.end}

mapper.update:
update sysuser


        username=#{username},


        password=#{password},


        idcard=#{idcard},


        email=#{email},


        gender=#{gender},


        qq=#{qq},


        weixin=#{weixin}


        regtime=#{regtime}


WHERE id=#{id}
